import React, { useState, useEffect } from "react";
import SeatsSelection from "./SeatsSelection";

function BookingPage({ routeId }) {
  const [selectedSeats, setSelectedSeats] = useState([]);
  const [showConfirm, setShowConfirm] = useState(false);
  const [bookingSuccess, setBookingSuccess] = useState(null);

  const handleBook = () => {
    if (selectedSeats.length === 0) {
      alert("Tafadhali chagua seat moja au zaidi!");
      return;
    }
    setShowConfirm(true); // show popup
  };

  const confirmBooking = () => {
    fetch("http://127.0.0.1:8000/api/bookings/", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ route: routeId, seats: selectedSeats }),
    })
      .then((res) => res.json())
      .then((data) => {
        console.log(data);
        setBookingSuccess(data.success);
        setShowConfirm(false);
        setSelectedSeats([]);
      });
  };

  return (
    <div>
      <h2>Book your seats</h2>
      <SeatsSelection
        routeId={routeId}
        selectedSeats={selectedSeats}
        setSelectedSeats={setSelectedSeats}
      />
      <button onClick={handleBook}>Book Now</button>

      {showConfirm && (
        <div className="popup">
          <h3>Confirm Booking</h3>
          <p>Selected seats: {selectedSeats.join(", ")}</p>
          <button onClick={confirmBooking}>Yes, Confirm</button>
          <button onClick={() => setShowConfirm(false)}>Cancel</button>
        </div>
      )}

      {bookingSuccess !== null && (
        <p>{bookingSuccess ? "Booking Successful!" : "Booking Failed!"}</p>
      )}
    </div>
  );
}

export default BookingPage;
