import React, { useState } from "react";
import SeatsSelection from "./SeatsSelection";

const BookingPage = () => {
  const [seats, setSeats] = useState([
    { id: 1, number: "A1", booked: false },
    { id: 2, number: "A2", booked: true },
    { id: 3, number: "A3", booked: false },
    { id: 4, number: "A4", booked: false },
  ]);

  const [selectedSeat, setSelectedSeat] = useState(null);

  const handleSelect = (seatId) => {
    setSelectedSeat(seatId);
  };

  const handleBookTicket = () => {
    if (!selectedSeat) {
      alert("Please select a seat first!");
      return;
    }

    // Mark the seat as booked
    setSeats((prevSeats) =>
      prevSeats.map((seat) =>
        seat.id === selectedSeat ? { ...seat, booked: true } : seat
      )
    );

    alert(`Seat ${selectedSeat} booked successfully!`);
    setSelectedSeat(null);
  };

  return (
    <div>
      <h2>Select Your Seat</h2>
      <SeatsSelection seats={seats} onSelect={handleSelect} />

      <button
        onClick={handleBookTicket}
        style={{
          marginTop: "20px",
          padding: "10px 20px",
          backgroundColor: selectedSeat ? "blue" : "gray",
          color: "white",
          border: "none",
          borderRadius: "5px",
          cursor: selectedSeat ? "pointer" : "not-allowed",
        }}
        disabled={!selectedSeat}
      >
        {selectedSeat ? `Book Seat ${selectedSeat}` : "Select a Seat"}
      </button>
    </div>
  );
};

export default BookingPage;
