// src/components/SeatsSelection.js
import React from "react";

const SeatsSelection = ({ seats, onSelect }) => {
  return (
    <div style={{ display: "flex", flexWrap: "wrap", maxWidth: "400px" }}>
      {Array.isArray(seats) && seats.map((seat) => (
        <button
          key={seat.id}
          style={{
            backgroundColor: seat.booked ? "red" : "green",
            color: "white",
            cursor: seat.booked ? "not-allowed" : "pointer",
            margin: "5px",
            padding: "10px",
            border: "none",
            borderRadius: "5px",
          }}
          onClick={() => onSelect(seat.id)}
          disabled={seat.booked}
        >
          {seat.number}
        </button>
      ))}
    </div>
  );
};

export default SeatsSelection;
