import React, { useState, useEffect } from 'react';
import api from './api';
import './Dashboard.css';

function UserDashboard({ user }) {
  const [schedules, setSchedules] = useState([]);
  const [bookings, setBookings] = useState([]);
  const [selectedSchedule, setSelectedSchedule] = useState(null);
  const [selectedSeats, setSelectedSeats] = useState([]);
  const [loading, setLoading] = useState(true);
  const [activeTab, setActiveTab] = useState('search');
  const [error, setError] = useState('');

  useEffect(() => {
    fetchSchedules();
    fetchBookings();
  }, []);

  const fetchSchedules = async () => {
    try {
      const response = await api.get('/schedules/');
      setSchedules(response.data);
    } catch (err) {
      setError('Failed to load schedules');
    } finally {
      setLoading(false);
    }
  };

  const fetchBookings = async () => {
    try {
      const response = await api.get('/bookings/');
      setBookings(response.data);
    } catch (err) {
      console.error('Failed to load bookings');
    }
  };

  const handleSelectSchedule = async (schedule) => {
    setSelectedSchedule(schedule);
    try {
      const response = await api.get(`/schedules/${schedule.id}/available_seats/`);
      setSelectedSeats(response.data.available_seats);
    } catch (err) {
      setError('Failed to load available seats');
    }
  };

  const handleBookSeat = async (seatNumber) => {
    try {
      await api.post('/bookings/', {
        schedule: selectedSchedule.id,
        seat_number: seatNumber,
      });
      alert(`Seat ${seatNumber} booked successfully!`);
      fetchBookings();
      handleSelectSchedule(selectedSchedule);
      setActiveTab('bookings');
    } catch (err) {
      setError(err.response?.data?.non_field_errors?.[0] || 'Failed to book seat');
    }
  };

  const handleCancelBooking = async (bookingId) => {
    try {
      await api.delete(`/bookings/${bookingId}/`);
      alert('Booking cancelled successfully!');
      fetchBookings();
    } catch (err) {
      setError('Failed to cancel booking');
    }
  };

  if (loading) {
    return <div className="loading">Loading...</div>;
  }

  return (
    <div className="dashboard">
      <div className="dashboard-header">
        <h1>Welcome, {user.username}!</h1>
        <p>Bus Ticket Booking System</p>
      </div>

      <div className="dashboard-tabs">
        <button
          className={activeTab === 'search' ? 'tab-btn active' : 'tab-btn'}
          onClick={() => setActiveTab('search')}
        >
          Search & Book
        </button>
        <button
          className={activeTab === 'bookings' ? 'tab-btn active' : 'tab-btn'}
          onClick={() => setActiveTab('bookings')}
        >
          My Bookings ({bookings.length})
        </button>
      </div>

      {error && <div className="error-message">{error}</div>}

      {activeTab === 'search' && (
        <div className="tab-content">
          <h2>Available Routes</h2>
          <div className="schedules-grid">
            {schedules.length === 0 ? (
              <p>No schedules available</p>
            ) : (
              schedules.map((schedule) => (
                <div key={schedule.id} className="schedule-card">
                  <div className="schedule-info">
                    <h3>
                      {schedule.route_details?.from_location} â†’ {schedule.route_details?.to_location}
                    </h3>
                    <p>
                      <strong>Bus:</strong> {schedule.bus_number} ({schedule.bus_name})
                    </p>
                    <p>
                      <strong>Departure:</strong> {new Date(schedule.departure_time).toLocaleString()}
                    </p>
                    <p>
                      <strong>Price:</strong> TZS {schedule.price}
                    </p>
                    <p>
                      <strong>Available Seats:</strong> {schedule.available_seats}
                    </p>
                  </div>
                  <button
                    className="btn-primary"
                    onClick={() => handleSelectSchedule(schedule)}
                  >
                    {selectedSchedule?.id === schedule.id ? 'Selected' : 'Select'}
                  </button>
                </div>
              ))
            )}
          </div>

          {selectedSchedule && (
            <div className="seats-selection">
              <h2>Select Your Seat</h2>
              <p>
                Bus: {selectedSchedule.bus_name} | Available Seats: {selectedSeats.length}
              </p>
              <div className="seats-grid">
                {Array.from({ length: selectedSchedule.bus_total_seats }, (_, i) => i + 1).map(
                  (seatNum) => (
                    <button
                      key={seatNum}
                      className={`seat ${
                        selectedSeats.includes(seatNum) ? 'available' : 'booked'
                      }`}
                      disabled={!selectedSeats.includes(seatNum)}
                      onClick={() => handleBookSeat(seatNum)}
                    >
                      {seatNum}
                    </button>
                  )
                )}
              </div>
            </div>
          )}
        </div>
      )}

      {activeTab === 'bookings' && (
        <div className="tab-content">
          <h2>My Bookings</h2>
          {bookings.length === 0 ? (
            <p>You have no bookings yet.</p>
          ) : (
            <div className="bookings-list">
              {bookings.map((booking) => (
                <div key={booking.id} className="booking-card">
                  <div className="booking-info">
                    <p>
                      <strong>Schedule:</strong> {booking.schedule_route}
                    </p>
                    <p>
                      <strong>Seat:</strong> {booking.seat_number}
                    </p>
                    <p>
                      <strong>Booked At:</strong> {new Date(booking.booked_at).toLocaleString()}
                    </p>
                  </div>
                  <button
                    className="btn-danger"
                    onClick={() => handleCancelBooking(booking.id)}
                  >
                    Cancel
                  </button>
                </div>
              ))}
            </div>
          )}
        </div>
      )}
    </div>
  );
}

export default UserDashboard;
