import React, { useState, useEffect } from 'react';
import api from './api';
import './Dashboard.css';

function AdminDashboard({ user }) {
  const [activeTab, setActiveTab] = useState('routes');
  const [routes, setRoutes] = useState([]);
  const [buses, setBuses] = useState([]);
  const [schedules, setSchedules] = useState([]);
  const [bookings, setBookings] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  // Form states
  const [formData, setFormData] = useState({});
  const [editingId, setEditingId] = useState(null);

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {
    try {
      const [routesRes, busesRes, schedulesRes, bookingsRes] = await Promise.all([
        api.get('/routes/'),
        api.get('/buses/'),
        api.get('/schedules/'),
        api.get('/bookings/'),
      ]);
      setRoutes(routesRes.data);
      setBuses(busesRes.data);
      setSchedules(schedulesRes.data);
      setBookings(bookingsRes.data);
    } catch (err) {
      setError('Failed to load data');
    } finally {
      setLoading(false);
    }
  };

  const handleAddRoute = async (e) => {
    e.preventDefault();
    try {
      if (editingId) {
        await api.put(`/routes/${editingId}/`, formData);
        setEditingId(null);
      } else {
        await api.post('/routes/', formData);
      }
      setFormData({});
      fetchData();
    } catch (err) {
      setError('Failed to save route');
    }
  };

  const handleDeleteRoute = async (id) => {
    try {
      await api.delete(`/routes/${id}/`);
      fetchData();
    } catch (err) {
      setError('Failed to delete route');
    }
  };

  const handleAddBus = async (e) => {
    e.preventDefault();
    try {
      if (editingId) {
        await api.put(`/buses/${editingId}/`, formData);
        setEditingId(null);
      } else {
        await api.post('/buses/', formData);
      }
      setFormData({});
      fetchData();
    } catch (err) {
      setError('Failed to save bus');
    }
  };

  const handleDeleteBus = async (id) => {
    try {
      await api.delete(`/buses/${id}/`);
      fetchData();
    } catch (err) {
      setError('Failed to delete bus');
    }
  };

  const handleAddSchedule = async (e) => {
    e.preventDefault();
    try {
      if (editingId) {
        await api.put(`/schedules/${editingId}/`, formData);
        setEditingId(null);
      } else {
        await api.post('/schedules/', formData);
      }
      setFormData({});
      fetchData();
    } catch (err) {
      setError('Failed to save schedule');
    }
  };

  const handleDeleteSchedule = async (id) => {
    try {
      await api.delete(`/schedules/${id}/`);
      fetchData();
    } catch (err) {
      setError('Failed to delete schedule');
    }
  };

  if (loading) {
    return <div className="loading">Loading...</div>;
  }

  return (
    <div className="dashboard">
      <div className="dashboard-header">
        <h1>Admin Dashboard</h1>
        <p>Welcome, {user.username} (Administrator)</p>
      </div>

      <div className="dashboard-tabs">
        <button
          className={activeTab === 'routes' ? 'tab-btn active' : 'tab-btn'}
          onClick={() => setActiveTab('routes')}
        >
          Routes
        </button>
        <button
          className={activeTab === 'buses' ? 'tab-btn active' : 'tab-btn'}
          onClick={() => setActiveTab('buses')}
        >
          Buses
        </button>
        <button
          className={activeTab === 'schedules' ? 'tab-btn active' : 'tab-btn'}
          onClick={() => setActiveTab('schedules')}
        >
          Schedules
        </button>
        <button
          className={activeTab === 'bookings' ? 'tab-btn active' : 'tab-btn'}
          onClick={() => setActiveTab('bookings')}
        >
          Bookings
        </button>
      </div>

      {error && <div className="error-message">{error}</div>}

      {activeTab === 'routes' && (
        <div className="tab-content">
          <h2>Manage Routes</h2>
          <form onSubmit={handleAddRoute} className="form-box">
            <input
              type="text"
              placeholder="From Location"
              value={formData.from_location || ''}
              onChange={(e) =>
                setFormData({ ...formData, from_location: e.target.value })
              }
              required
            />
            <input
              type="text"
              placeholder="To Location"
              value={formData.to_location || ''}
              onChange={(e) =>
                setFormData({ ...formData, to_location: e.target.value })
              }
              required
            />
            <input
              type="number"
              placeholder="Distance (km)"
              value={formData.distance || ''}
              onChange={(e) =>
                setFormData({ ...formData, distance: e.target.value })
              }
              required
            />
            <button type="submit" className="btn-primary">
              {editingId ? 'Update Route' : 'Add Route'}
            </button>
          </form>
          <div className="items-list">
            {routes.map((route) => (
              <div key={route.id} className="item-card">
                <div>
                  <h4>
                    {route.from_location} → {route.to_location}
                  </h4>
                  <p>Distance: {route.distance} km</p>
                </div>
                <button
                  className="btn-danger"
                  onClick={() => handleDeleteRoute(route.id)}
                >
                  Delete
                </button>
              </div>
            ))}
          </div>
        </div>
      )}

      {activeTab === 'buses' && (
        <div className="tab-content">
          <h2>Manage Buses</h2>
          <form onSubmit={handleAddBus} className="form-box">
            <input
              type="text"
              placeholder="Bus Number"
              value={formData.bus_number || ''}
              onChange={(e) =>
                setFormData({ ...formData, bus_number: e.target.value })
              }
              required
            />
            <input
              type="text"
              placeholder="Bus Name"
              value={formData.bus_name || ''}
              onChange={(e) =>
                setFormData({ ...formData, bus_name: e.target.value })
              }
              required
            />
            <input
              type="number"
              placeholder="Total Seats"
              value={formData.total_seats || ''}
              onChange={(e) =>
                setFormData({ ...formData, total_seats: e.target.value })
              }
              required
            />
            <button type="submit" className="btn-primary">
              {editingId ? 'Update Bus' : 'Add Bus'}
            </button>
          </form>
          <div className="items-list">
            {buses.map((bus) => (
              <div key={bus.id} className="item-card">
                <div>
                  <h4>{bus.bus_name}</h4>
                  <p>Bus Number: {bus.bus_number}</p>
                  <p>Seats: {bus.total_seats}</p>
                </div>
                <button
                  className="btn-danger"
                  onClick={() => handleDeleteBus(bus.id)}
                >
                  Delete
                </button>
              </div>
            ))}
          </div>
        </div>
      )}

      {activeTab === 'schedules' && (
        <div className="tab-content">
          <h2>Manage Schedules</h2>
          <form onSubmit={handleAddSchedule} className="form-box">
            <select
              value={formData.route || ''}
              onChange={(e) =>
                setFormData({ ...formData, route: e.target.value })
              }
              required
            >
              <option value="">Select Route</option>
              {routes.map((route) => (
                <option key={route.id} value={route.id}>
                  {route.from_location} → {route.to_location}
                </option>
              ))}
            </select>
            <select
              value={formData.bus || ''}
              onChange={(e) => setFormData({ ...formData, bus: e.target.value })}
              required
            >
              <option value="">Select Bus</option>
              {buses.map((bus) => (
                <option key={bus.id} value={bus.id}>
                  {bus.bus_name}
                </option>
              ))}
            </select>
            <input
              type="datetime-local"
              value={formData.departure_time || ''}
              onChange={(e) =>
                setFormData({ ...formData, departure_time: e.target.value })
              }
              required
            />
            <input
              type="number"
              placeholder="Price"
              value={formData.price || ''}
              onChange={(e) =>
                setFormData({ ...formData, price: e.target.value })
              }
              required
            />
            <input
              type="number"
              placeholder="Available Seats"
              value={formData.available_seats || ''}
              onChange={(e) =>
                setFormData({ ...formData, available_seats: e.target.value })
              }
              required
            />
            <button type="submit" className="btn-primary">
              {editingId ? 'Update Schedule' : 'Add Schedule'}
            </button>
          </form>
          <div className="items-list">
            {schedules.map((schedule) => (
              <div key={schedule.id} className="item-card">
                <div>
                  <h4>Route ID: {schedule.route}</h4>
                  <p>Departure: {new Date(schedule.departure_time).toLocaleString()}</p>
                  <p>Price: TZS {schedule.price}</p>
                  <p>Available Seats: {schedule.available_seats}</p>
                </div>
                <button
                  className="btn-danger"
                  onClick={() => handleDeleteSchedule(schedule.id)}
                >
                  Delete
                </button>
              </div>
            ))}
          </div>
        </div>
      )}

      {activeTab === 'bookings' && (
        <div className="tab-content">
          <h2>All Bookings</h2>
          <div className="items-list">
            {bookings.length === 0 ? (
              <p>No bookings yet</p>
            ) : (
              bookings.map((booking) => (
                <div key={booking.id} className="item-card">
                  <div>
                    <h4>Booking ID: {booking.id}</h4>
                    <p>User: {booking.user_username}</p>
                    <p>Seat: {booking.seat_number}</p>
                    <p>Booked At: {new Date(booking.booked_at).toLocaleString()}</p>
                  </div>
                </div>
              ))
            )}
          </div>
        </div>
      )}
    </div>
  );
}

export default AdminDashboard;
